var $ = require('jquery');
var ko = require('knockout');

var textToEl = function(htmlText) {
    return $(htmlText)[0];
};

var koApply = function(viewModel, elText, parentSelector) {
    var el = textToEl(elText);
    ko.applyBindings(viewModel, el);
    parentSelector && $(parentSelector).append(el);
    return el;
};

exports.textToEl = textToEl;
exports.koApply = koApply;